pragma solidity ^0.8.15;

import "./HCToken.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

//The interfaces are commented out because of the version conflict
interface USDCInterface {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
}

// contract USDTInterface {
//     event Transfer(address indexed from, address indexed to, uint256 value);
//     event Approval(address indexed owner, address indexed spender, uint256 value);
//     function totalSupply() external view returns (uint256);
//     function balanceOf(address account) external view returns (uint256);
//     function transfer(address to, uint256 amount) external returns (bool);
//     function allowance(address owner, address spender) external view returns (uint256);
//     function approve(address spender, uint256 amount) external returns (bool);
//     function transferFrom(address from, address to, uint256 amount) external returns (bool);
// }

// contract DAITokenInterface {
//     event Transfer(address indexed from, address indexed to, uint256 value);
//     event Approval(address indexed owner, address indexed spender, uint256 value);
//     function totalSupply() external view returns (uint256);
//     function balanceOf(address account) external view returns (uint256);
//     function transfer(address to, uint256 amount) external returns (bool);
//     function allowance(address owner, address spender) external view returns (uint256);
//     function approve(address spender, uint256 amount) external returns (bool);
//     function transferFrom(address from, address to, uint256 amount) external returns (bool);
// }


contract LoanProfile is Ownable {

    event updatedCollectedAmount(uint newAmount);

    string public name = "Mortgage A";
    address public homeOwner = 0xC6A3dd9e9D73Eb3e66669534Ed21ee169aEd7f14;
    uint public loanAmount;
    uint public timePeriod;
    uint public timeCreated;
    uint public collectedAmount;
    uint public loanId;
    uint public timeValidated; 
    HCToken public token;

    address public USDCAddress = 0x07865c6E87B9F70255377e024ace6630C1Eaa37F;
    USDCInterface USDC = USDCInterface(USDCAddress);

    // address public DAITokenAddress = 0x31F42841c2db5173425b5223809CF3A38FEde360;
    // DAITokenInterface DAI = DAITokenInterface(DAITokenAddress);

    // address public USDTAddress = 0x110a13FC3efE6A245B50102D2d79B3E76125Ae83;
    // USDTInterface USDT = USDTInterface(USDTAddress);

    address[] public investors;

    mapping(address => uint) public investedAmount;
    mapping(address => bool) public hasInvested;
    mapping(address => bool) public isInvesting;

    constructor(uint _loanId, uint _loanAmount, uint _timePeriod, address HCTokenAddress) public onlyOwner {
        loanId = _loanId;
        loanAmount = _loanAmount;
        timePeriod = _timePeriod;
        timeCreated = block.timestamp;
        collectedAmount = 0;
        token = HCToken(HCTokenAddress);
        token.mint(address(this),100); //Mint some tokens for the address
    }

    function deposit(uint _amount) public {
        require(_amount > 0, "Amount cannot be 0");
        require(block.timestamp<(timeCreated+timePeriod),"The loan period is over");
        //deposit stable coin
        USDC.transferFrom(msg.sender, address(this), _amount);
        //update investedAmount
        investedAmount[msg.sender] = investedAmount[msg.sender] + _amount;
        //add to investors array if havent stake 
        if(!hasInvested[msg.sender]) {
            investors.push(msg.sender);
        } 
        //update staking status
        isInvesting[msg.sender] = true;
        hasInvested[msg.sender] = true;
        //update collected amount
        collectedAmount= collectedAmount + _amount;
        exchange(msg.sender, _amount);        
        emit updatedCollectedAmount(collectedAmount);       
    }

    //Exchange amount deposited for HCtoken
    function exchange(address to,uint _amount) internal{
        token.transfer(to, rate() * _amount);
    }

     /**
     * @dev Refund the amount invested if the loan wasn't completed
     */
    function refund() public payable{
        require(block.timestamp >= (timeCreated + timePeriod),"The loan period hasn't passed yet");
        require(isInvesting[msg.sender],"You're not an investor");
        uint balance = investedAmount[msg.sender];
        require(msg.value == balance,"Amount of tokens invalid");
        USDC.transfer(msg.sender, balance);
        token.transferFrom(msg.sender,address(this),balance);
    }

//This function is external because I'm not expecting tocall this internally
    function loanFulfilled() external onlyOwner {
        require(collectedAmount == loanAmount);
        timeValidated=block.timestamp;
        USDC.transferFrom(address(this), homeOwner, collectedAmount);
    }

    //Went with S-Curve function: price = a/(1+exp^-b( tokenSupply -c)) 
    //(a) Price Ceiling 
    //(b) dictates how aggressively the curve accelerates in the growth phase,
    //(c) determines the inflection point of the curve
    function rate() internal returns(uint){
        return 1;
    }

}
